import { Link } from "react-router-dom";
import star from './../assets/images/star.svg'
import './../styles/Favorite.scss'
export const Favorites = ({ favorite }) => {
  return (
    <div className="favorite-page container">
      <h4>Избранное</h4>
      <div className="headhpones">
        <h4>Наушники</h4>
        <div className="d-flex flex-wrap gap-4 my-5 justify-content-between">
        {favorite.map((item) => (
          <div className="favorite-page__card position-relative d-flex align-items-center justify-content-center flex-column">
            {/* {active ? (
              <img
                onClick={selectFavoriteItem}
                src={favClick}
                alt="Избранное"
                className={"favorite position-absolute"}
              />
            ) : (
              <img
                onClick={selectFavoriteItem}
                src={fav}
                alt="Избранное"
                className="favorite position-absolute"
              />
            )} */}

            <Link to={`/catalog-item/${item.id}/${item.title}`}>
              <figure className="d-flex align-items-center justify-content-center ">
                <img src={item.img} alt={item.title} />
              </figure>
            </Link>

            <div className="headphones-price d-flex justify-content-between w-100">
              <p>{item.title}</p>
              <div className="d-flex flex-column ">
                <span>{item.newPrice} ₸</span>
                <span>{item.oldPrice} ₸</span>
              </div>
            </div>
            <p className="d-flex align-items-center justify-content-start w-100">
              <img className="m-0" src={star} alt="" />
              4.7
            </p>
          </div>
        ))}
        </div>
      </div>
      {favorite.length ? <div>Favorites</div> : <p>EMPTY</p>}
    </div>

  );
};
