import { useState } from "react";
import arrowDownSvg from "./../../../assets/images/arrowDown.svg";
import './Select.scss'

export const Select = ({ selectType, text }) => {
  const [active, setActive] = useState(false);
  const [selectedOptions, setSelectedOptions] = useState(null);

  const handleOptionSelect = (option) => {
    setSelectedOptions(option);
    setActive(false);
  };

  return (
    <div className="select-popup">
      <div className="select-header" onClick={()=>setActive(!active)}>
        <img src={selectType} alt="" />
        <span className="selected-option">{selectedOptions || text}</span>
        <img className="arrow-icon" src={arrowDownSvg} alt="" />
      </div>
      {
        active
      }
      <div className="select-options">
        <div className="option" onClick={() => handleOptionSelect("options 1")}>
          option 1
        </div>
        <div className="option" onClick={() => handleOptionSelect("options 1")}>
          option 1
        </div>
      </div>
    </div>
  );
};
