import { useState } from "react";
import arrowDownSvg from "./../../../assets/images/arrowDown.svg";

export const Select = ({ selectType, text }) => {
  const [active, setActive] = useState(false);
  const [selectedOptions, setSelectedOptions] = useState(null)

  const handleOptionSelect = (option) => {
    setSelectedOptions(option)
    setActive(false);
  };

  return (
    <div className="select-popup">
      <div className="menu_nime" onClick={selectedOptions(!active)}>
        <img src={selectType} alt="" />
        <span className="">{selectedOptions || text}</span>
        <img src={arrowDownSvg} alt="" />
      </div>
      <div className="select-options">
        <div className="option" on>option 1</div>
        <div className="option">option 1</div>
      </div>
    </div>
  );
};
