import "./../styles/CatalogItems.scss";
import addCart from "./../assets/images/cart.svg";
import { useParams } from "react-router-dom";
import CatalogItemImage from "../components/catalogItem/CatalogItemImage";

export const CatalogItem = ({ handleAddToCart, db }) => {
  window.scrollTo(0, 0);

  const { id } = useParams();

  const headphonesItems = db[0].headphones.find(
    (item) => item.id === parseInt(id)
  );
  const casesItems = db[1].cases.find((item) => item.id === parseInt(id));
  const airpodsItems = db[2].airpods.find((item) => item.id === parseInt(id));

  // console.log(headphonesItems);

  const onAddToCart = () => {
    let newObj = null;
    if (headphonesItems) {
      newObj = {
        id: headphonesItems.id,
        img: headphonesItems.img,
        title: headphonesItems.title,
        price: headphonesItems.newPrice,
        count: 1,
      };
    } else if (casesItems) {
      newObj = {
        id: casesItems.id,
        img: casesItems.img,
        title: casesItems.description,
        price: casesItems.newPrice,
        count: 1,
      };
    } else if (airpodsItems) {
      newObj = {
        id: airpodsItems.id,
        img: airpodsItems.img,
        title: airpodsItems.name,
        price: airpodsItems.newPrice,
        count: 1,
      };
    }

    handleAddToCart(newObj);
  };

  return (
    <div className="catalog-item container">
      <h4>Автодержатель</h4>
      <CatalogItemImage
        headphonesItems={headphonesItems}
        casesItems={casesItems}
        airpodsItems={airpodsItems}
      />

    </div>
  );
};
